export const avifSupport = new Promise<boolean>(resolve => {
	const image = new Image();
	console.log("avifSupport");
	image.addEventListener('error', () => resolve(false));
	image.addEventListener('load', () => resolve(image.width === 2));
	image.src =  "data:image/avif;base64,";
}).catch(() => false);

export default avifSupport;
